const ethers = require('ethers');
const Web3 = require('web3');

const privateKey = "PRIVATE_KEY";
const provider = new ethers.providers.JsonRpcProvider("RPC_URL");
const wallet = new ethers.Wallet(privateKey, provider);

const parameters =
    {
        chainId : 137,
        from: "0xCbdd4B5C47100A1b06078ad2048F66F134Ec85C4",
        to: "0x1111111254fb6c44bac0bed2854e76f90643097d",
        data: "",
        gasPrice: 35000000000,
        gasLimit:500000,
        nonce : 222
    }
  ;

const call = async ()=>{

    let rawTransaction = await wallet.signTransaction(parameters);
    let a = await provider.sendTransaction(rawTransaction)
console.log("TX Hash : ",a.hash)
}

call()
var web3Provider = new Web3.providers.HttpProvider("RPC_URL");
var web3 = new Web3(web3Provider);
web3.eth.getTransactionCount("WALLET_ADDRESS").then((result) => {
  console.log("nonce : ",result);
});